mapper.select:
select * from s_user  where 1= 1

 and state =#{state}


 and create_time >=#{startTime}

 
 and create_time <=#{endTime}


 and user_name like CONCAT('%',#{userName},'%')
  order by create_time desc  limit #{page},#{rows}

select count(*) from s_user where 1= 1

 and create_time >=#{startTime}

 
 and create_time <=#{endTime}

 
 and state =#{state}


 and user_name  like CONCAT('%',#{userName},'%')
